import com.wavemaker.runtime.file.model.Downloadable;

import com.testing15thnov_stage.dbscenarios.models.query.*;

@Service
public class DbscenariosQueryExecutorServiceImpl implements DbscenariosQueryExecutorService {

    private static final Logger LOGGER = LoggerFactory.getLogger(DbscenariosQueryExecutorServiceImpl.class);

    @Autowired
    @Qualifier("dbscenariosWMQueryExecutor")
    private WMQueryExecutor queryExecutor;

    @Transactional(readOnly = true, value = "dbscenariosTransactionManager")
    @Override
    public Page<SvInoperatorResponse> executeSV_INOperator(List<Integer> pkid, Pageable pageable) {
        Map params = new HashMap(1);

        params.put("PKID", pkid);

        return queryExecutor.executeNamedQuery("SV_INOperator", params, SvInoperatorResponse.class, pageable);
    }

    @Transactional(readOnly = true, value = "dbscenariosTransactionManager")
    @Override
    public InputStream getBlobColContentForSV_INOperator(Integer pkId, List<Integer> pkid) throws EntityNotFoundException {
        Map params = new HashMap(1);

        params.put("pkId", pkId);
        params.put("PKID", pkid);

        SvInoperatorResponse _result =  queryExecutor.executeNamedQuery("SV_INOperator__identifier", params, SvInoperatorResponse.class);
        if(_result.getBlobCol() == null) {
            LOGGER.debug("Blob content not exists for blobCol in query SV_INOperator");
            throw new BlobContentNotFoundException("Blob content not found for blobCol in query SV_INOperator");
        }
        return new ByteArrayInputStream(_result.getBlobCol());
    }

    @Transactional(readOnly = true, value = "dbscenariosTransactionManager")
    @Override
    public Downloadable exportSV_INOperator(ExportType exportType, List<Integer> pkid, Pageable pageable) {
        Map params = new HashMap(1);

        params.put("PKID", pkid);

        return queryExecutor.exportNamedQueryData("SV_INOperator", params, exportType, SvInoperatorResponse.class, pageable);
    }

}


